mapper.select:
SELECT oi.id, oi.CODE, oi.order_name, oi.shop_id, oi.create_date, oi.remark, oi.order_status, oi.user_ids, oi.pay_date, oi.finish_date, oi.order_price, oi.user_num, oi.commodity_id, oi.user_id, oi.address_id, ci.`code` AS commodityCode, ci.`name`, ci.type, ci.images, ci.content, ci.on_put AS onPut, si.introduction, si.name AS shopName, si.images AS shopAvatar, si.tag, ui.user_name AS userName, ui.avatar AS avatar, ai.address, ai.`name` AS taskUserName, ai.phone, ct.type_name FROM order_info oi LEFT JOIN user_info ui ON ( ui.id = oi.user_id ) LEFT JOIN commodity_info ci ON ( ci.id = oi.commodity_id ) LEFT JOIN commodity_type ct ON ( ct.id = ci.`type` ) LEFT JOIN shop_info si ON ( si.id = oi.shop_id ) LEFT JOIN address_info ai ON ( ai.id = oi.address_id ) WHERE 1 = 1
AND ui.user_name LIKE CONCAT('%',#{orderInfo.userName},'%')
AND su.user_name LIKE CONCAT('%',#{orderInfo.shopName},'%')
AND ci.`name` LIKE CONCAT('%',#{orderInfo.commodityName},'%')
AND oi.order_status = #{orderInfo.orderStatus}
AND ci.type = #{orderInfo.type}
AND si.sys_user_id = #{orderInfo.shopId}
ORDER BY oi.create_date desc

SELECT DATE_FORMAT( spo.days, '%m-%d' ) AS days, IFNULL(COUNT(oi.id), 0) AS count FROM ( SELECT DATE_SUB( curdate(), INTERVAL + 0 DAY ) days UNION SELECT DATE_SUB( curdate(), INTERVAL + 1 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 2 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 3 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 4 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 5 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 6 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 7 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 8 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 9 DAY )) spo LEFT JOIN order_info oi ON ( DATE_FORMAT( oi.create_date, '%Y-%m-%d' ) = DATE_FORMAT( spo.days, '%Y-%m-%d' ) AND (oi.order_status = 1 or oi.order_status = 3)
AND oi.shop_id = #{shopId}
) GROUP BY days ORDER BY days ASC

SELECT DATE_FORMAT( spo.days, '%m-%d' ) AS days, IFNULL(SUM(oi.order_price * oi.user_num), 0) AS price FROM ( SELECT DATE_SUB( curdate(), INTERVAL + 0 DAY ) days UNION SELECT DATE_SUB( curdate(), INTERVAL + 1 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 2 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 3 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 4 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 5 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 6 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 7 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 8 DAY ) UNION SELECT DATE_SUB( curdate(), INTERVAL + 9 DAY )) spo LEFT JOIN order_info oi ON ( DATE_FORMAT( oi.create_date, '%Y-%m-%d' ) = DATE_FORMAT( spo.days, '%Y-%m-%d' ) AND (oi.order_status = 1 or oi.order_status = 3)
AND oi.shop_id = #{shopId}
) GROUP BY days ORDER BY days ASC

select oi.* from order_info oi where (oi.order_status = 1 or oi.order_status = 3)
AND oi.shop_id = #{shopId}
AND DATE_FORMAT( oi.create_date, '%Y%m' ) = DATE_FORMAT( CURDATE(), '%Y%m')

select oi.* from order_info oi where (oi.order_status = 1 or oi.order_status = 3)
AND oi.shop_id = #{shopId}
AND DATE_FORMAT( oi.create_date, '%Y' ) = DATE_FORMAT( CURDATE(), '%Y')
